        private void dataGridView1_CellClick(object sender, DataGridViewCellEventArgs e)
        {
            if (e.RowIndex >= 0) // Tıklanan satır geçerliyse
            {
                label1.Text = dataGridView1.Rows[e.RowIndex].Cells["ID"].Value.ToString(); // ID hücresini al
                DataGridViewRow row = dataGridView1.Rows[e.RowIndex];
                textBox1.Text = row.Cells["KitapAdi"].Value?.ToString(); // Örnek: Kitap adını doldurma
                textBox2.Text = row.Cells["YazarAdi"].Value?.ToString(); // Örnek: Yazar adını doldurma
                textBox3.Text = row.Cells["KitapTurKod"].Value?.ToString(); // Örnek: Tür kodunu doldurma
                label1.Text = row.Cells["ID"].Value?.ToString(); // Seçilen Kitap ID'sini doldurma
            }
        }

